import {observable, computed, reaction, action} from 'mobx';

export default class CounterStore {
	@observable state = 0;

	@action
	increment (title) {
		this.state = this.state + 1;
	}

	@action
	decrement (checked) {
		this.state = this.state - 1;
	}
}
