import {observable, computed, reaction, action} from 'mobx';

export default class CounterStore {
	@observable counter = 0;

	// @action.bound
	increment () {
		this.counter = this.counter + 1;
	}

	// @action.bound
	decrement () {
		this.counter = this.counter - 1;
	}

	// @action.bound
	incrementIfOdd() {
		if (this.counter % 2 !== 0) {
			this.increment();
		}
	  }

	  incrementAsync() {
		setTimeout(this.increment, 2000)
	  }
}
