import {observable, computed, reaction, action} from 'mobx';

export default class CounterStore {
	@observable counter = 0;

	@action
	increment = () => {
		this.counter = this.counter + 1;
	};

	@action
	decrement = () => {
		this.counter = this.counter - 1;
	}

	@action
	incrementIfOdd = () => {
		if (this.counter % 2 !== 0) {
			this.increment();
		}
	  }

	  @action
	  incrementAsync = () => {
		setTimeout(this.increment, 1000)
	  }
}
